import json
import pandas as pd
import re

# ------------------------------------------------------------------
# helper to turn an Enterococcus cell into a float or None
def parse_enterococcus(value):
    """
    Returns float if the value is parseable, otherwise None.
      '<10'   -> 5.0
      '< 10'  -> 5.0
      '>1000' -> 1000.0
      numeric string -> float
      anything else  -> None
    """
    if pd.isna(value):
        return None

    s = str(value).strip()

    # patterns for < and >
    lt_match = re.match(r'^<\s*([0-9.]+)$', s)
    gt_match = re.match(r'^>\s*([0-9.]+)$', s)
    num_match = re.match(r'^([0-9.]+)$', s)

    if lt_match:
        try:
            return float(lt_match.group(1)) / 2.0
        except ValueError:
            return None
    elif gt_match:
        try:
            return float(gt_match.group(1))
        except ValueError:
            return None
    elif num_match:
        try:
            return float(num_match.group(1))
        except ValueError:
            return None
    else:
        return None

