import json
import pandas as pd
from pathlib import Path

# path list shortened to the four candidate precipitation tables
precip_files = {
    "monthly_precipitations_chatham.csv": "/home/gridsan/elai/Kramabench/data/environment/input/monthly_precipitations_chatham.csv",
    "monthly_precipitations_ashburnham.csv": "/home/gridsan/elai/Kramabench/data/environment/input/monthly_precipitations_ashburnham.csv",
    "monthly_precipitations_amherst.csv": "/home/gridsan/elai/Kramabench/data/environment/input/monthly_precipitations_amherst.csv",
    "monthly_precipitations_boston.csv": "/home/gridsan/elai/Kramabench/data/environment/input/monthly_precipitations_boston.csv"
}

def has_year_precipitation(path):
    try:
        df = pd.read_csv(path)
    except Exception:
        # try reading with different delimiter if needed
        df = pd.read_csv(path, sep=";")
    # basic check: must have a 'Year' column and at least 4 month columns
    cols = [c.lower() for c in df.columns]
    if "year" in cols:
        month_cols = [m for m in cols if m in
                      ['jan','feb','mar','apr','may','jun','jul','aug','sep','oct','nov','dec']]
        # also require an Annual / total column
        if len(month_cols) >= 4 and ("annual" in cols or "total" in cols):
            return True
    return False

precip_file_with_year = None
for name, path in precip_files.items():
    if Path(path).exists() and has_year_precipitation(path):
        precip_file_with_year = name
        break

print(json.dumps(
    {
        "subtask-1": precip_file_with_year,
        "main-task": precip_file_with_year  # same answer for this simple question
    },
    indent=4
))

